	/**
	 * methode for simplify test in console
	 * 
	 * @param grid
	 * @param size
	 * @param dictionary
	 * @param printWord
	 * @param printgrid
	 */
	private static void test(String grid, int size, LexicographicTree dictionary, boolean printWord,
			boolean printgrid) {
		long startTime = System.currentTimeMillis();
		Boggle boggle = new Boggle(size, grid, dictionary);
		System.out.println("Grid size : " + size + "X" + size);
		if (printgrid) {
			System.out.println("Boggle grid :");
			System.out.println(boggle.toString());
		}

		// Solve grid
		System.out.println("Solving Boggle grid...");
		Set<String> results = boggle.solve();
		long solveTime = System.currentTimeMillis();
		System.out.println("Duration : " + (solveTime - startTime) / 1000.0);
		System.out.println("Number of words found : " + results.size());
		if (printWord)
			System.out.println(new TreeSet<String>(results).toString());
	}

