package boggle;

import java.nio.file.NoSuchFileException;
import java.util.HashSet;
import java.util.Random;
import java.util.Set;
import java.util.TreeSet;
import tree.LexicographicTree;

public class Boggle {

	private static final int[] ROWS = { -1, -1, -1, 0, 0, 1, 1, 1 };
	private static final int[] COLS = { -1, 0, 1, -1, 1, -1, 0, 1 };

	private LexicographicTree dictionary;
	private char[][] board;
	private int size;

	/*
	 * CONSTRUCTORS
	 */

	/**
	 * Constructor : creates a Boggle grid filled with random letters.
	 * 
	 * @param size The size of the squared grid
	 * @param dict A dictionary of allowed words
	 */
	public Boggle(int size, LexicographicTree dict) {
		this(size, geneStrRandom(size*size), dict);
	}

	/**
	 * Constructor : creates a Boggle grid filled with the supplied letters.
	 * 
	 * @param size    The size of the squared grid
	 * @param letters A string containing the (size x size) letters used to fill the
	 *                grid
	 * @param dict    A dictionary of allowed words
	 */
	public Boggle(int size, String letters, LexicographicTree dict) {
		this.dictionary = dict;
		if (size <= 0 || letters.length() != size * size)
			throw new RuntimeException("wrong parameters:\n\tsize:" + size + "\n\tletters:" + letters);
		this.size = size;
		this.board = new char[size][size];
		for (int i = 0; i < board.length; i++) {
			for (int j = 0; j < board.length; j++) {
				board[j][i] = letters.charAt(j * size + i);
			}
		}
	}

	/*
	 * PUBLIC METHODS
	 */

	/**
	 * Returns the letters in the Boggle grid.
	 * 
	 * @return a string of letters
	 */
	public String letters() {
		String letter = "";
		for (char[] ch : board) {
			for (char c : ch) {
				letter += c;
			}
		}
		return letter;
	}

	/**
	 * Determines if a word can be found in the Boggle grid.
	 * 
	 * @param word a word
	 * @return true if the word is present, false otherwise
	 */
	public boolean contains(String word) {
		for (int i = 0; i < board.length; i++) {
			for (int j = 0; j < board[i].length; j++) {
				if(board[i][j] == word.charAt(0)) {
					boolean[][] visited = new boolean[size][size];
					if(searchForStr(i, j, "", visited, word))
							return true;
				}				
			}
		}
		
		
		return false;
	}

	/**
	 * Searches for words in the Boggle grid.
	 * 
	 * @return the set of found words
	 */
	public Set<String> solve() {
		Set<String> allWord = new HashSet<>();
		for (int i = 0; i < board.length; i++) {
			for (int j = 0; j < board[i].length; j++) {
				boolean[][] visited = new boolean[size][size];
				searchForOneLetter(i, j, "", allWord, visited);
			}
		}
		
		return allWord;
	}

	/**
	 * Returns a textual representation of the Boggle grid.
	 * 
	 * @return a textual representation of the Boggle grid
	 */
	public String toString() {
		String format = "";
		for (char[] cs : board) {
			for (char c : cs) {
				format += c + " ";
			}
			format += "\n";
		}
		return format;
	}

	/*
	 * PRIVATE METHODS
	 */

	private void searchForOneLetter(int col, int row, String currentWord, Set<String> allWord, boolean[][] visited) {

		if (row < 0 || row >= size || col < 0 || col >= size || visited[row][col]) {
			return;
		}

		currentWord += board[col][row];

		int num = dictionary.hasPrefixOrWord(currentWord);

		if (num == 1 && currentWord.length() >= 3) {
			allWord.add(currentWord);
		}

		if (num == -1)
			return;

		visited[row][col] = true;

		for (int i = 0; i < ROWS.length; i++) {
			searchForOneLetter(col + COLS[i], row + ROWS[i], currentWord, allWord, visited);
		}
		visited[row][col] = false;
	}
	
	
	private boolean searchForStr(int col, int row, String currentWord, boolean[][] visited, String str) {
		if (row < 0 || row >= size || col < 0 || col >= size || visited[row][col]) {
			return false;
		}
		
		currentWord += board[col][row];
		
		if(str.equals(currentWord))
			return true;
		
		if(!str.startsWith(currentWord))
			return false;
		
		boolean isIn = false;	
		visited[row][col] = true;
		
		for (int i = 0; i < ROWS.length; i++) {
			isIn |= searchForStr(col + COLS[i], row + ROWS[i], currentWord, visited, str);
			if(isIn)
				break;
		}
		visited[row][col] = false;
		return isIn;
	}
	
	private static String geneStrRandom(int size) {
		//find on https://www.apprendre-en-ligne.net/crypto/stat/francais.html
		//mon but est de le genere en fonction du dictionnaire
		final double[] frequencyLetter = { 8.15, 0.97, 3.15, 3.73, 17.39, 1.12, 0.97, 
				0.85, 7.31, 0.45, 0.02, 5.69, 2.87,
				7.12, 5.28, 2.80, 1.21, 6.64, 8.14, 
				7.22, 6.38, 1.64, 0.03, 0.41, 0.28, 0.15 };
		final String alphabet = "abcdefghijklmnopkrstuvwxyz";
		String strGenereted = "";
		Random random = new Random();
		for (int i = 0; i < size; i++) {
			int index = getRandomIndex(frequencyLetter, random);
			strGenereted += alphabet.charAt(index);
        }
		
		return strGenereted;
	}
	
	private static int getRandomIndex(double[] frequencies, Random random) {
        double total = 0.0;
        for (double freq : frequencies) {
            total += freq;
        }

        double randomNumber = random.nextDouble() * total;
        double cumulative = 0.0;
        for (int i = 0; i < frequencies.length; i++) {
            cumulative += frequencies[i];
            if (randomNumber < cumulative) {
                return i;
            }
        }

        return -1;
    }
	
	/*
	 * MAIN PROGRAM
	 */

	public static void main(String[] args) throws NoSuchFileException {
		long startTime = System.currentTimeMillis();
		System.out.println("Loading dictionary...");
		LexicographicTree dictionary = new LexicographicTree("mots/dictionnaire_FR_sans_accents.txt");
		long loadDictTime = System.currentTimeMillis();
		System.out.println("Duration : " + (loadDictTime - startTime) / 1000.0);
		System.out.println("Number of words : " + dictionary.size());

		System.out.println();
		System.out.println("-------------------------------");
		System.out.println();

		String grid4x4 = "rhreypcswnsntego";
		test(grid4x4, 4, dictionary, false, false);

		System.out.println();
		System.out.println("-------------------------------");
		System.out.println();

		String grid10x10 = "eymmccsrltjttsdiraoarliuniepeousrcgoiseerreeistiedtomcteevcmkaualilaretneerectresieenspgizeoeceecuds";
		test(grid10x10, 10, dictionary, false, false);

		System.out.println();
		System.out.println("-------------------------------");
		System.out.println();

		String grid20x20 = "";
		test(grid20x20, 20, dictionary, false, false);

		System.out.println();
		System.out.println("-------------------------------");
		System.out.println();

		String grid50x50 = "";
		test(grid50x50, 50, dictionary, false, false);

		System.out.println();
		System.out.println("-------------------------------");
		System.out.println();

		String grid100x100 = "";
		test(grid100x100, 100, dictionary, false, false);

		System.out.println();
		System.out.println("-------------------------------");
		System.out.println();

		String grid150x150 = "";
		test(grid150x150, 150, dictionary, false, false);
		
		int size = 1000;
		test(geneStrRandom(size*size), size, dictionary, false, false);
		

	}

	/**
	 * methode for simplify test in console
	 * 
	 * @param grid
	 * @param size
	 * @param dictionary
	 * @param printWord
	 * @param printgrid
	 */
	private static void test(String grid, int size, LexicographicTree dictionary, boolean printWord,
			boolean printgrid) {
		long startTime = System.currentTimeMillis();
		Boggle boggle = new Boggle(size, grid, dictionary);
		System.out.println("Grid size : " + size + "X" + size);
		if (printgrid) {
			System.out.println("Boggle grid :");
			System.out.println(boggle.toString());
		}

		// Solve grid
		System.out.println("Solving Boggle grid...");
		Set<String> results = boggle.solve();
		long solveTime = System.currentTimeMillis();
		System.out.println("Duration : " + (solveTime - startTime) / 1000.0);
		System.out.println("Number of words found : " + results.size());
		if (printWord)
			System.out.println(new TreeSet<String>(results).toString());
	}

}
